// Process and structure the AI-generated Business Operating System
const analysisData = $('âš™ï¸ Prepare B-OS Analysis').item.json;
const aiResponse = $('ğŸ¤– AI B-OS Generation').item.json;

// Extract the generated B-OS structure
const businessOS = aiResponse.choices?.[0]?.message?.content || 'B-OS generation failed';
const usage = aiResponse.usage || {};

// Parse and validate the B-OS structure
const bosLines = businessOS.split('\n');
const domains = [];
let currentDomain = null;

// Extract 0-9 domains from the generated content
bosLines.forEach(line => {
  const domainMatch = line.match(/^[â”œâ””]â”€\s*(\d)\.(.*?)$/);  
  if (domainMatch) {
    if (currentDomain) domains.push(currentDomain);
    currentDomain = {
      number: parseInt(domainMatch[1]),
      name: domainMatch[2].trim(),
      subdomains: [],
      content: []
    };
  } else if (line.match(/^â”‚\s+[â”œâ””]â”€\s*(\d\.\d)/) && currentDomain) {
    const subMatch = line.match(/^â”‚\s+[â”œâ””]â”€\s*(\d\.\d)\s+(.*?)$/);
    if (subMatch) {
      currentDomain.subdomains.push({
        number: subMatch[1],
        name: subMatch[2].trim()
      });
    }
  } else if (currentDomain && line.trim()) {
    currentDomain.content.push(line.trim());
  }
});
if (currentDomain) domains.push(currentDomain);

// Generate implementation roadmap
const implementationPhases = [
  {
    phase: 1,
    name: 'Foundation Setup',
    domains: [0, 8, 9],
    timeline: 'Week 1-2',
    description: 'Establish core foundation, tech infrastructure, and governance'
  },
  {
    phase: 2, 
    name: 'Market & Product',
    domains: [1, 5],
    timeline: 'Week 3-4',
    description: 'Define market positioning and product strategy'
  },
  {
    phase: 3,
    name: 'Revenue Engine',
    domains: [2, 3],
    timeline: 'Week 5-6', 
    description: 'Build growth and sales systems'
  },
  {
    phase: 4,
    name: 'Operations & Scale',
    domains: [4, 6, 7],
    timeline: 'Week 7-8',
    description: 'Implement delivery, finance, and people systems'
  }
];

// Create platform creation specifications
const platformSpecs = {
  github: {
    repository_name: `${analysisData.company_name.toLowerCase().replace(/\s+/g, '-')}-business-os`,
    description: `Business Operating System for ${analysisData.company_name}`,
    folders: domains.map(domain => ({
      name: `${domain.number}-${domain.name.toLowerCase().replace(/[^a-z0-9]/g, '-')}`,
      readme_content: `# ${domain.number}. ${domain.name}\n\n${domain.content.join('\n')}`,
      subfolders: domain.subdomains.map(sub => sub.name.toLowerCase().replace(/[^a-z0-9]/g, '-'))
    }))
  },
  notion: {
    workspace_name: `${analysisData.company_name} Business OS`,
    databases: domains.map(domain => ({
      name: `${domain.number}-${domain.name}`,
      properties: ['Status', 'Owner', 'Due Date', 'Priority', 'Resources']
    }))
  },
  slack: {
    channels: domains.map(domain => ({
      name: `${domain.number.toString().padStart(2, '0')}-${domain.name.toLowerCase().replace(/[^a-z0-9]/g, '-')}`,
      description: `${domain.name} - ${domain.content[0] || 'Business domain discussions'}`.substring(0, 250)
    }))
  }
};

const results = {
  // Core B-OS data
  company_name: analysisData.company_name,
  business_operating_system: businessOS,
  structured_domains: domains,
  domain_count: domains.length,

  // Implementation planning
  implementation_phases: implementationPhases,
  estimated_completion: '8 weeks',

  // Platform specifications
  platform_creation_specs: platformSpecs,

  // AI processing metrics
  generation_metrics: {
    tokens_used: usage.total_tokens || 0,
    processing_time_ms: new Date().getTime() - new Date(analysisData.timestamp).getTime(),
    model_used: 'ai/llama3.2:1B-Q8_0',
    content_length: businessOS.length
  },

  // Status and next steps
  status: 'b-os_generated',
  next_actions: [
    'Review generated Business Operating System structure',
    'Customize domains based on specific business needs', 
    'Deploy structure to GitHub, Notion, and Slack',
    'Implement daily entropy management automation',
    'Begin systematic business organization'
  ],

  // Export formats
  exports: {
    markdown: businessOS,
    json_structure: domains,
    implementation_guide: implementationPhases
  },

  // Quality assessment
  quality_metrics: {
    domains_generated: domains.length,
    expected_domains: 10,
    completeness: Math.round((domains.length / 10) * 100),
    ready_for_deployment: domains.length >= 8
  }
};

console.log(`ğŸ—ï¸ B-OS generated: ${domains.length} domains, ${usage.total_tokens || 0} tokens`);
console.log(`ğŸ“Š Completeness: ${results.quality_metrics.completeness}%`);
console.log(`ğŸš€ Ready for deployment: ${results.quality_metrics.ready_for_deployment}`);

return [{ json: results }];

