// Deploy Status - Log the deployment start
const inputData = $json.body || $json;
const companyName = inputData.company_name || 'Unknown Company';
const githubOwner = inputData.github_owner || 'unknown';
const repositoryName = inputData.repository_name || 'unknown-repo';

console.log('=== GITHUB DEPLOYMENT STARTED ===');
console.log('Company:', companyName);
console.log('GitHub Owner:', githubOwner);
console.log('Repository:', repositoryName);
console.log('Domains:', (inputData.domains || []).length);

return [{
  company_name: companyName,
  github_owner: githubOwner,
  repository_name: repositoryName,
  domains: inputData.domains || [],
  deployment_status: 'started',
  timestamp: new Date().toISOString()
}];

// Prepare File Deployment - Generate all files for GitHub
const data = $json;
const companyName = data.company_name;
const githubOwner = data.github_owner;
const repositoryName = data.repository_name;
const domains = data.domains || [];

console.log('=== PREPARING FILE DEPLOYMENT ===');
console.log('Generating files for:', companyName);

// Generate main README.md content
const mainReadme = `# ${companyName} - Business Operating System

ðŸ—ï¸ **Systematic Business Organization**

This repository contains the complete Business Operating System (B-OS) for ${companyName}, organized using the systematic 0-9 framework for maximum clarity and zero entropy.

## ðŸ“‹ Business Domains

${domains.map(domain => {
  const subdomainText = domain.subdomains ? 
    domain.subdomains.map(sub => `- ${sub.number}: ${sub.name}`).join('
') : '';

  return `### ${domain.number} - ${domain.name}
${domain.content ? domain.content.join(', ') : 'Core business area'}

**Subdomains:**
${subdomainText}
`;
}).join('
')}

## ðŸš€ Usage

This systematic organization eliminates the "entropy tax" by providing:
- Clear structure for all business information
- Consistent navigation across domains
- Systematic templates for each area
- AI-powered insights and automation

**Generated by:** AI-Powered Entropy Management System
**Framework:** 0-9 Business Operating System
**Date:** ${new Date().toISOString().split('T')[0]}`;

// Generate domain files
const domainFiles = domains.map(domain => {
  const subdomainsList = domain.subdomains ? 
    Object.entries(domain.subdomains).map(([key, value]) => `## ${key}: ${value}

Content for ${value} goes here.
`).join('
') : '';

  return {
    path: `${domain.number}-${domain.name.toLowerCase().replace(/\s+/g, '-')}/README.md`,
    content: `# ${domain.number} - ${domain.name}

${domain.content ? domain.content.join(' ') : 'Domain description'}

${subdomainsList}

---
*Part of ${companyName} Business Operating System*`
  };
});

// Create structure files
const structureFiles = [
  {
    path: '.github/workflows/daily-entropy.yml',
    content: `name: Business OS Maintenance

on:
  schedule:
    - cron: '0 9 * * *'  # Daily at 9 AM
  workflow_dispatch:

jobs:
  entropy-management:
    runs-on: ubuntu-latest
    steps:
      - uses: actions/checkout@v3
      - name: Process Daily Entropy
        run: echo "Daily business organization automation"`
  },
  {
    path: '.gitignore',
    content: `# Business OS
*.tmp
.env
.DS_Store
node_modules/
*.log`
  },
  {
    path: 'STRUCTURE.md',
    content: `# ${companyName} - Business Structure

## 0-9 Framework

${domains.map(d => `- **${d.number}**: ${d.name}`).join('
')}

## Navigation

Each domain contains:
- README.md with overview
- Subdomain structure
- Templates and examples
- Action items and insights`
  }
];

console.log('Files prepared:', domainFiles.length + structureFiles.length + 1);

return [{
  company_name: companyName,
  github_owner: githubOwner,
  repository_name: repositoryName,
  domains: domains,
  main_readme: mainReadme,
  domain_files: domainFiles,
  structure_files: structureFiles,
  total_files: domainFiles.length + structureFiles.length + 1,
  deployment_status: 'files_prepared'
}];

parameters.arguments:
={{ 
  const fileIndex = $json["$index"];
  const file = $json.domain_files[fileIndex];
  const safeContent = file.content.replace(/'/g, "'\"'\"'");
  const tempFile = `/tmp/domain_${fileIndex}_${$json.repository_name}.md`;

  return `-c "echo '${safeContent}' > ${tempFile} && docker mcp tools call create_or_update_file owner='${$json.github_owner}' repo='${$json.repository_name}' path='${file.path}' content=\"$(cat ${tempFile})\" message='Add domain: ${file.path}' branch='main' && rm -f ${tempFile}"`;
}}

={{ 
  const commands = $json.structure_files.map((file, index) => {
    const safeContent = file.content.replace(/'/g, "'\"'\"'");
    const tempFile = `/tmp/structure_${index}_${$json.repository_name}.md`;
    return `echo '${safeContent}' > ${tempFile} && docker mcp tools call create_or_update_file owner='${$json.github_owner}' repo='${$json.repository_name}' path='${file.path}' content=\"$(cat ${tempFile})\" message='Add structure: ${file.path}' branch='main' && rm -f ${tempFile}`;
  }).join(' && ');

  return `-c "${commands}"`;
}}

parameters.functionCode:
// Deployment Complete - Generate final response
const data = $json;

console.log('=== DEPLOYMENT COMPLETE ===');
console.log('Repository:', `https://github.com/${data.github_owner}/${data.repository_name}`);

const response = {
  status: 'success',
  message: `GitHub repository created successfully for ${data.company_name}`,
  repository: {
    name: data.repository_name,
    owner: data.github_owner,
    url: `https://github.com/${data.github_owner}/${data.repository_name}`,
    readme_url: `https://github.com/${data.github_owner}/${data.repository_name}/blob/main/README.md`
  },
  structure: {
    domains: (data.domains || []).length,
    total_files: data.total_files || 0,
    framework: '0-9 Business Operating System'
  },
  next_steps: [
    'Visit the repository to explore the structure',
    'Customize domain content for your business',
    'Enable GitHub Actions for automated maintenance',
    'Connect with daily entropy management system'
  ],
  timestamp: new Date().toISOString()
};

return [response];
