const data = $input.all();
const company = data[0].json.company_name;
const domains = data[0].json.domains;

// Generate main README content
let readme = `# ${company} - Business Operating System\n\n`;
readme += `## ðŸ—ï¸ Systematic Business Organization\n\n`;
readme += `This repository contains the complete Business Operating System (B-OS) for ${company}, organized using the systematic 0-9 framework for maximum clarity and zero entropy.\n\n`;

// Add domain overview
readme += `## ðŸ“Š Business Domains\n\n`;
domains.forEach(domain => {
  readme += `### ${domain.number} - ${domain.name}\n`;
  readme += `${domain.content ? domain.content.join(', ') : 'Core business area'}\n\n`;

  if (domain.subdomains && domain.subdomains.length > 0) {
    readme += `**Subdomains:**\n`;
    domain.subdomains.forEach(sub => {
      readme += `- ${sub.number}: ${sub.name}\n`;
    });
    readme += `\n`;
  }
});

readme += `## ðŸŽ¯ Quick Navigation\n\n`;
domains.forEach(domain => {
  const folderName = `${domain.number}-${domain.name.replace(/\s+/g, '-')}`;
  readme += `- [${domain.number} - ${domain.name}](./${folderName}/)\n`;
});

readme += `\n## ðŸš€ Usage\n\nThis systematic organization eliminates the "entropy tax" by providing:\n- Clear structure for all business information\n- Consistent navigation across domains\n- Systematic templates for each area\n- AI-powered insights and automation\n\n**Generated by:** AI-Powered Entropy Management System\n**Framework:** 0-9 Business Operating System\n**Date:** ${new Date().toISOString().split('T')[0]}`;

// Generate file list for batch creation
const files = [];

domains.forEach(domain => {
  const folderName = `${domain.number}-${domain.name.replace(/\s+/g, '-')}`;

  // Domain README
  let domainReadme = `# ${domain.number} - ${domain.name}\n\n`;
  domainReadme += `## Overview\n\n${domain.content ? domain.content.join(', ') : 'Core business domain for systematic organization.'}\n\n`;

  if (domain.subdomains && domain.subdomains.length > 0) {
    domainReadme += `## Subdomains\n\n`;
    domain.subdomains.forEach(sub => {
      domainReadme += `### ${sub.number} - ${sub.name}\n`;
      domainReadme += `[View Details](./${sub.number.replace('.', '-')}-${sub.name.replace(/\s+/g, '-')}/README.md)\n\n`;
    });
  }

  domainReadme += `## Templates\n\n- [ ] Strategy Document\n- [ ] Process Documentation\n- [ ] Resource Inventory\n- [ ] Action Items\n\n**Last Updated:** ${new Date().toISOString().split('T')[0]}`;

  files.push({
    path: `${folderName}/README.md`,
    content: domainReadme,
    type: 'domain'
  });

  // Subdomain files
  if (domain.subdomains && domain.subdomains.length > 0) {
    domain.subdomains.forEach(sub => {
      const subFolderName = `${sub.number.replace('.', '-')}-${sub.name.replace(/\s+/g, '-')}`;
      let subReadme = `# ${sub.number} - ${sub.name}\n\n`;
      subReadme += `## Purpose\n\nSystematic organization for ${sub.name} within the ${domain.name} domain.\n\n`;
      subReadme += `## Structure\n\n- [ ] Documentation/\n- [ ] Templates/\n- [ ] Resources/\n- [ ] Processes/\n\n`;
      subReadme += `## Quick Actions\n\n- [ ] Review current state\n- [ ] Update documentation\n- [ ] Process improvements\n- [ ] Resource optimization\n\n`;
      subReadme += `**Domain:** ${domain.number} - ${domain.name}\n**Subdomain:** ${sub.number} - ${sub.name}\n**Updated:** ${new Date().toISOString().split('T')[0]}`;

      files.push({
        path: `${folderName}/${subFolderName}/README.md`,
        content: subReadme,
        type: 'subdomain'
      });
    });
  }
});

return [{
  json: {
    company_name: company,
    repository_name: data[0].json.repository_name,
    github_owner: data[0].json.github_owner,
    main_readme: readme,
    files: files,
    total_files: files.length + 1
  }
}];
