// Prepare GitHub file deployment with B-OS structure (assumes repository exists)
const inputData = $('🚀 GitHub Deploy Trigger').item.json.body;

// Extract B-OS data
const companyName = inputData.company_name || 'Your Company';
const repositoryName = inputData.repository_name;
const githubOwner = inputData.github_owner;

// Handle both business_os (from AI Builder) and domains (manual input)
let domains = [];
if (inputData.business_os) {
  domains = Object.keys(inputData.business_os).map(key => {
    const domain = inputData.business_os[key];
    return {
      number: parseInt(key),
      name: domain.name,
      content: [domain.description],
      subdomains: domain.subdomains ? Object.keys(domain.subdomains).map(subKey => ({
        number: subKey,
        name: domain.subdomains[subKey]
      })) : [],
      ai_insights: domain.ai_insights || {},
      action_items: domain.action_items || []
    };
  });
} else {
  domains = inputData.domains || inputData.structured_domains || [];
}

// Validate required inputs
if (!githubOwner || !repositoryName) {
  throw new Error('github_owner and repository_name are required');
}

// Generate folder structure based on 0-9 framework
const folderStructure = domains.map(domain => {
  const folderName = `${domain.number.toString().padStart(2, '0')}-${domain.name.toLowerCase().replace(/[^a-z0-9]/g, '-')}`;

  let readmeContent = `# ${domain.number}. ${domain.name}\n\n## Overview\n${domain.content?.join('\n') || 'Business domain documentation and resources.'}\n\n`;

  if (domain.ai_insights && Object.keys(domain.ai_insights).length > 0) {
    readmeContent += `## AI Insights\n\n`;
    Object.entries(domain.ai_insights).forEach(([key, value]) => {
      if (Array.isArray(value)) {
        readmeContent += `**${key.replace(/_/g, ' ').toUpperCase()}:**\n${value.map(v => `- ${v}`).join('\n')}\n\n`;
      } else {
        readmeContent += `**${key.replace(/_/g, ' ').toUpperCase()}:** ${value}\n\n`;
      }
    });
  }

  if (domain.subdomains && domain.subdomains.length > 0) {
    readmeContent += `## Structure\n\n${domain.subdomains.map(sub => `- **${sub.number}** ${sub.name}`).join('\n')}\n\n`;
  }

  if (domain.action_items && domain.action_items.length > 0) {
    readmeContent += `## Next Actions\n\n${domain.action_items.map(item => `- [ ] ${item}`).join('\n')}\n\n`;
  }

  readmeContent += `## Resources\n\n- [ ] Documentation\n- [ ] Templates\n- [ ] Processes\n- [ ] Tools & Integrations\n\n---\n*Auto-generated by Entropy Management System*`;

  return {
    domain_number: domain.number,
    domain_name: domain.name,
    folder_name: folderName,
    folder_path: `${folderName}/`,
    readme: {
      path: `${folderName}/README.md`,
      content: readmeContent
    }
  };
});

// Create enhanced main repository README
const hasAiRoadmap = inputData.ai_roadmap && Object.keys(inputData.ai_roadmap).length > 0;
const hasAiIntelligence = inputData.business_intelligence && Object.keys(inputData.business_intelligence).length > 0;

let mainReadmeContent = `# ${companyName} - Business Operating System\n\n> Systematic business organization powered by AI-driven entropy management\n\n## 🎯 Overview\n\nThis repository contains the complete Business Operating System (B-OS) for ${companyName}, organized using the systematic 0-9 framework for maximum clarity and zero organizational overhead.\n\n`;

if (hasAiIntelligence) {
  const bi = inputData.business_intelligence;
  mainReadmeContent += `## 🤖 AI Business Intelligence\n\n**Business Focus:** ${bi.business_focus || 'Not specified'}\n\n**Core Value Proposition:** ${bi.core_value_proposition || 'Not specified'}\n\n**Business Model:** ${bi.business_model || 'Not specified'}\n\n`;
}

mainReadmeContent += `## 🏗️ Structure\n\n${domains.map(domain => `### ${domain.number}. ${domain.name}\n📁 [\`${domain.number.toString().padStart(2, '0')}-${domain.name.toLowerCase().replace(/[^a-z0-9]/g, '-')}/\`](./${domain.number.toString().padStart(2, '0')}-${domain.name.toLowerCase().replace(/[^a-z0-9]/g, '-')}/)\n${domain.content?.[0] || 'Business domain for ' + domain.name}\n`).join('\n')}`;

if (hasAiRoadmap) {
  mainReadmeContent += `\n\n## 🗺️ AI-Generated Roadmap\n\n${Object.entries(inputData.ai_roadmap).map(([phase, data]) => `### ${data.name}\n**Duration:** ${data.duration}\n**Focus:** ${data.focus}\n**Domains:** ${data.domains?.join(', ')}\n**Priority:** ${data.ai_priority}\n`).join('\n')}`;
}

mainReadmeContent += `\n\n## 🚀 Getting Started\n\n1. **Browse by Domain**: Navigate to any 0-9 numbered folder\n2. **Review Templates**: Each domain contains structured templates\n3. **Follow Processes**: Use established workflows in each area\n4. **Maintain Structure**: Add new content following the 0-9 framework\n\n## 🤖 Automation\n\nThis B-OS is maintained by an AI-powered entropy management system that:\n\n- ✅ Automatically organizes new business information\n- ✅ Maintains cross-platform consistency\n- ✅ Generates insights from organized data\n- ✅ Prevents organizational chaos\n\n---\n\n**Generated by**: MCP Docker Entropy Management System\n**Last Updated**: ${new Date().toISOString()}\n**Domains**: ${domains.length}/10\n**Status**: ${domains.length >= 8 ? '✅ Ready for Production' : '⚠️ In Development'}${hasAiIntelligence ? '\n**AI Model**: ' + (inputData.ai_model_used || 'AI-Enhanced') : ''}`;

const mainReadme = {
  path: 'README.md',
  content: mainReadmeContent
};

const allFiles = [
  mainReadme,
  ...folderStructure.map(folder => folder.readme)
];

const preparationData = {
  company_name: companyName,
  repository_name: repositoryName,
  github_owner: githubOwner,
  folder_structure: folderStructure,
  main_readme: mainReadme,
  all_files: allFiles,

  stats: {
    domains_count: domains.length,
    folders_to_create: folderStructure.length,
    total_files: allFiles.length,
    ai_enhanced: hasAiIntelligence
  },

  validation: {
    has_domains: domains.length > 0,
    has_github_owner: !!githubOwner,
    ready_for_deployment: domains.length > 0 && !!githubOwner && !!repositoryName
  },

  timestamp: new Date().toISOString()
};

console.log(`🐙 Preparing GitHub file deployment for: ${companyName}`);
console.log(`📁 Creating ${preparationData.stats.total_files} files in repository: ${repositoryName}`);
console.log(`✅ Ready: ${preparationData.validation.ready_for_deployment}`);
console.log(`🤖 AI Enhanced: ${preparationData.stats.ai_enhanced}`);

return [{ json: preparationData }];

// Final deployment summary
const deploymentData = $('⚙️ Prepare File Deployment').item.json;
const githubVerification = $('🔍 Verify GitHub Access').item.json;
const folderStructure = deploymentData.folder_structure;

let authenticatedUser = 'Unknown';
try {
  if (githubVerification.stdout) {
    const userInfo = JSON.parse(githubVerification.stdout);
    authenticatedUser = userInfo.login || 'Unknown';
  }
} catch (error) {
  console.log('Could not parse GitHub user info:', error.message);
}

const createdStructure = {
  deployment_status: 'completed',

  repository: {
    name: deploymentData.repository_name,
    owner: deploymentData.github_owner,
    url: `https://github.com/${deploymentData.github_owner}/${deploymentData.repository_name}`,
    updated_via: 'MCP Docker file operations'
  },

  github_verification: {
    authenticated_user: authenticatedUser,
    access_confirmed: !!githubVerification.stdout
  },

  domains_created: folderStructure.map(folder => ({
    domain: `${folder.domain_number}. ${folder.domain_name}`,
    folder_name: folder.folder_name,
    readme_path: folder.readme.path
  })),

  files_created: [
    deploymentData.main_readme.path,
    ...folderStructure.map(folder => folder.readme.path)
  ],

  creation_summary: {
    total_files: deploymentData.stats.total_files,
    domains_structured: folderStructure.length,
    repository_url: `https://github.com/${deploymentData.github_owner}/${deploymentData.repository_name}`,
    structure_type: '0-9 Systematic Business Framework',
    mcp_integration: 'MCP Docker file operations',
    ai_enhanced: deploymentData.stats.ai_enhanced || false
  },

  next_steps: [
    `Visit repository: https://github.com/${deploymentData.github_owner}/${deploymentData.repository_name}`,
    'Verify all domain folders are created',
    'Review generated Business OS structure',
    'Set up Notion workspace integration',
    'Configure Slack channels for each domain'
  ],

  completion_status: 'github_files_deployed_via_mcp',
  timestamp: new Date().toISOString()
};

console.log(`🎉 GitHub Business OS file deployment completed!`);
console.log(`📍 Repository: ${createdStructure.repository.url}`);
console.log(`👤 GitHub User: ${createdStructure.github_verification.authenticated_user}`);
console.log(`📁 Files created: ${createdStructure.files_created.length}`);
console.log(`🏗️ Domains: ${createdStructure.domains_created.length}`);
console.log(`🐳 MCP Integration: Active`);

return [{ json: createdStructure }];
