};

// Prepare GitHub API calls sequence
const deploymentPlan = {
  step1_create_repo: {
    action: 'create_repository',
    params: githubConfig
  },
  step2_main_readme: {
    action: 'create_file',
    params: {
      path: mainReadme.path,
      content: mainReadme.content,
      message: 'Initial commit: Business Operating System structure'
    }
  },
  step3_domain_folders: folderStructure.map(folder => ({
    action: 'create_file',
    params: {
      path: folder.readme.path,
      content: folder.readme.content,
      message: `Add ${folder.domain_name} domain structure`
    }
  })),
  step4_templates: folderStructure.flatMap(folder => 
    folder.subfolders.map(subfolder => ({
      action: 'create_file',
      params: {
        path: subfolder.template.path,
        content: subfolder.template.content,
        message: `Add template for ${subfolder.name}`
      }
    }))
  )
};

const preparationData = {
  company_name: companyName,
  repository_name: repositoryName,
  github_config: githubConfig,
  folder_structure: folderStructure,
  main_readme: mainReadme,
  deployment_plan: deploymentPlan,

  // Statistics
  stats: {
    domains_count: domains.length,
    folders_to_create: folderStructure.length,
    templates_to_create: folderStructure.reduce((sum, folder) => sum + folder.subfolders.length, 0),
    total_files: 1 + folderStructure.length + folderStructure.reduce((sum, folder) => sum + folder.subfolders.length, 0)
  },

  // Validation
  validation: {
    has_domains: domains.length > 0,
    has_github_owner: !!inputData.github_owner,
    ready_for_deployment: domains.length > 0 && !!inputData.github_owner
  },

  timestamp: new Date().toISOString()
};

console.log(`ğŸ™ Preparing GitHub deployment for: ${companyName}`);
console.log(`ğŸ“ Creating ${preparationData.stats.total_files} files across ${preparationData.stats.domains_count} domains`);
console.log(`âœ… Ready: ${preparationData.validation.ready_for_deployment}`);

