// Prepare business analysis prompt
const inputData = $input.all()[0].json;
const companyName = inputData.company_name || 'Your Company';
const documents = inputData.documents || [];

const prompt = `Create a systematic 0-9 business framework for ${companyName}.

Documents provided:
${documents.map(doc => `- ${doc.type}: ${doc.content}`).join('\n')}

Generate a structured business operating system with:
0. Foundation
1. Market Intelligence
2. Growth Engine
3. Sales Pipeline
4. Delivery & Success
5. Product Innovation
6. Finance & Admin
7. People & Culture
8. Technology Stack
9. Governance

For each domain, provide specific actionable recommendations.`;

return {
  company_name: companyName,
  ai_prompt: prompt,
  documents_count: documents.length
};
