// Process input data
const data = $input.first().json.body || $input.first().json;

// Extract required fields
const company = data.company_name || 'Test Company';
const repo = data.repository_name || 'test-repo';
const owner = data.github_owner || 'ajeetraina';
const domains = data.domains || [];

console.log(`Creating repo: ${owner}/${repo} for ${company}`);
console.log(`Domains: ${domains.length}`);

// Generate main README
let readme = `# ${company} - Business Operating System\n\n`;
readme += `## ğŸ—ï¸ Systematic Business Organization\n\n`;
readme += `This repository contains the Business Operating System for **${company}**.\n\n`;
readme += `**Created:** ${new Date().toISOString().split('T')[0]}\n`;
readme += `**Repository:** https://github.com/${owner}/${repo}\n\n`;

if (domains.length > 0) {
  readme += `## ğŸ“Š Business Domains\n\n`;
  domains.forEach(domain => {
    const num = domain.number || 'X';
    const name = domain.name || 'Domain';
    const folder = `${num}-${name.replace(/\s+/g, '-')}`;

    readme += `### ${num} - ${name}\n`;
    if (domain.content && Array.isArray(domain.content)) {
      readme += `**Focus:** ${domain.content.join(', ')}\n`;
    }
    readme += `- [ğŸ“ ${name}](./${folder}/)\n\n`;
  });

  readme += `## ğŸ¯ Quick Navigation\n\n`;
  domains.forEach(domain => {
    const folder = `${domain.number}-${domain.name.replace(/\s+/g, '-')}`;
    readme += `- [${domain.number} - ${domain.name}](./${folder}/)\n`;
  });
  readme += `\n`;
}

readme += `## ğŸš€ Features\n\n`;
readme += `- âœ… Systematic organization using 0-9 framework\n`;
readme += `- âœ… Clear navigation and structure\n`;
readme += `- âœ… AI-powered entropy management\n`;
readme += `- âœ… Scalable business operations\n\n`;

readme += `---\n`;
readme += `**Generated by:** Docker MCP + n8n Workflow\n`;
readme += `**Framework:** AI-Powered Entropy Management System`;

// Generate domain files
const files = [];
domains.forEach(domain => {
  const folder = `${domain.number}-${domain.name.replace(/\s+/g, '-')}`;

  let domainContent = `# ${domain.number} - ${domain.name}\n\n`;
  domainContent += `## Overview\n\n`;
  if (domain.content && Array.isArray(domain.content)) {
    domainContent += `**Core areas:** ${domain.content.join(', ')}\n\n`;
  }
  domainContent += `This domain organizes all ${domain.name.toLowerCase()} activities for ${company}.\n\n`;

  // Add subdomains if they exist
  if (domain.subdomains && Array.isArray(domain.subdomains)) {
    domainContent += `## Subdomains\n\n`;
    domain.subdomains.forEach(sub => {
      const subFolder = `${sub.number.replace('.', '-')}-${sub.name.replace(/\s+/g, '-')}`;
      domainContent += `### ${sub.number} - ${sub.name}\n`;
      domainContent += `- [ğŸ“‚ ${sub.name}](./${subFolder}/)\n\n`;

      // Create subdomain file
      files.push({
        path: `${folder}/${subFolder}/README.md`,
        content: `# ${sub.number} - ${sub.name}\n\n## Purpose\n\nSystematic organization for ${sub.name} within the ${domain.name} domain.\n\n## Structure\n\n- [ ] Documentation\n- [ ] Templates\n- [ ] Resources\n- [ ] Processes\n\n**Domain:** ${domain.number} - ${domain.name}\n**Updated:** ${new Date().toISOString().split('T')[0]}`
      });
    });
  }

  domainContent += `## Templates\n\n- [ ] Strategy Document\n- [ ] Process Documentation\n- [ ] Action Items\n\n**Last Updated:** ${new Date().toISOString().split('T')[0]}`;

  files.push({
    path: `${folder}/README.md`,
    content: domainContent
  });
});

console.log(`Generated ${files.length} files`);

return {
  json: {
    company_name: company,
    repository_name: repo,
    github_owner: owner,
    main_readme: readme,
    files: files
  }
};
